mapper.update:
UPDATE news SET state = 1 WHERE news_id IN
#{newsId}
AND state = 0

UPDATE notices
SET state = 1
WHERE notices_id IN

  #{noticesId}

AND state = 0
AND type = #{type}

mapper.delete:
DELETE FROM news
WHERE news_id IN

  #{newsId}

DELETE FROM notices
WHERE notices_id IN

  #{noticesId}

AND type = #{type}
