mapper.select:
SELECT pa.activity_id 'activityId' ,pa.name 'name', pa.branch_id 'branchId' , pb.branch_name 'branchName' , pa.name , pa.create_id 'createId' , up.name 'createName' , pa.create_time 'createTime' , pa.num , COUNT(CASE WHEN (pau.check_state=1) THEN pau.user_id END) 'alreadyJoinNum' , COUNT(CASE WHEN (pau.check_state=0) THEN pau.user_id END) 'applyNum' , pa.registration_start 'registrationStart' , pa.registration_end 'registrationEnd', pa.activity_start 'activityStart' , pa.activity_end 'activityEnd' FROM party_activity pa LEFT JOIN partybranch pb ON pa.branch_id = pb.branch_id LEFT JOIN party_activity_user pau ON pa.activity_id = pau.activity_id LEFT JOIN user_person_info up ON up.user_id = pa.create_id WHERE pa.is_delete = 0
AND pa.branch_id = #{branchId}
GROUP BY pa.activity_id

SELECT a.user_id 'userId', upa.name 'name', r.role_name 'roleName' , pb.branch_name 'branchName' , a.phone_num 'phoneNum', a.submit_time 'applyTime' , a.check_time 'checkTime', upaa.name 'checkName' , a.check_state 'checkState' FROM party_activity_user a LEFT JOIN user_party_info upa ON a.user_id = upa.user_id LEFT JOIN user_login ul ON a.user_id = ul.user_id LEFT JOIN role r ON r.role_id = ul.role_id LEFT JOIN partybranch pb ON pb.branch_id = upa.branch_id LEFT JOIN user_party_info upaa ON a.check_id = upaa.user_id
WHERE a.activity_id = #{activityId}
WHERE a.check_state = #{checkState}
AND a.check_state = #{checkState}
ORDER BY ABS(a.check_state) ASC , a.submit_time DESC

mapper.delete:
UPDATE party_activity
SET is_delete = 1
WHERE activity_id IN

  #{activityId}


    AND branch_id = #{branchId}
